import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/products/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setProduct(data);
        // Default image — either main product or first variant
        setSelectedImage(data.variants?.[0]?.image || data.image);
      })
      .catch((err) => console.error("Error fetching product:", err));
  }, [id]);

  if (!product) return <div className="text-center p-8">Loading...</div>;

  // Handle color click
  const handleColorClick = (colorName) => {
    const matchedVariant = product.variants.find((variant) =>
      variant.title.toLowerCase().includes(colorName.toLowerCase())
    );

    if (matchedVariant) {
      setSelectedImage(matchedVariant.image);
    }
  };

  return (
    <div class="flex flex-col md:flex-row items-center md:items-end p-6 gap-8 max-w-6xl mx-auto">
      {/* Product Image + Color Dots */}
      <div className="flex-1">
        <img
          src={selectedImage}
          alt={product.name}
          className="w-full h-[420px] object-contain rounded-lg shadow-lg transition-all duration-300 ease-in-out"
        />

        {/* Color selection dots */}
        <div className="flex justify-center gap-3 mt-4">
          {product.finishes?.map((finish, idx) => (
            <div
              key={idx}
              onClick={() => handleColorClick(finish.name)}
              style={{ backgroundColor: finish.colorHex }}
              className={`w-6 h-6 rounded-full cursor-pointer border-2 transition-all duration-200 
              ${
                selectedImage?.toLowerCase().includes(finish.name.toLowerCase())
                  ? "border-blue-500 scale-110"
                  : "border-gray-300 hover:border-gray-500"
              }`}
            ></div>
          ))}
        </div>
      </div>

      {/* Product Details */}
      <div className="flex-1">
        <h2 className="text-3xl font-bold">{product.name}</h2>
        <p className="text-gray-500 mt-2">{product.brand}</p>
        <p className="text-lg text-gray-700 mt-4">{product.description}</p>
        <p className="text-2xl font-semibold text-blue-700 mt-4">
          ₹{product.price}
        </p>

        {/* EMI Plans */}
        <div className="mt-6">
          <h3 className="text-xl font-semibold">EMI Plans</h3>
          <ul className="mt-2 space-y-2">
            {product.emiPlans?.map((plan, index) => (
              <li
                key={index}
                className="border rounded-lg p-2 flex justify-between text-gray-700"
              >
                <span>{plan.tenureMonths} months</span>
                <span>{plan.interestRatePercent}% interest</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
