import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [selectedColor, setSelectedColor] = useState(0);

  useEffect(() => {
    fetch(`http://localhost:5000/api/products/${id}`)
      .then((res) => res.json())
      .then((data) => setProduct(data))
      .catch((err) => console.error("Error fetching product:", err));
  }, [id]);

  if (!product) return <div className="text-center py-20">Loading...</div>;

  const { name, variants, emiPlans, badges } = product;
  const selectedVariant = variants[selectedColor];

  return (
    <div className="flex flex-col md:flex-row justify-center items-start gap-10 p-8 bg-gray-50 min-h-screen">
      {/* LEFT SECTION */}
      <div className="bg-white p-6 rounded-2xl shadow-lg w-full md:w-1/2 relative">
        {/* üî¥ Badges */}
        {badges && badges.length > 0 && (
          <div className="absolute top-0 left-0 flex gap-2">
            {badges.map((badge, i) => (
              <span
                key={i}
                className="bg-red-600 text-white text-xs font-semibold px-3 py-1 rounded-full shadow"
              >
                {badge}
              </span>
            ))}
          </div>
        )}

        {/* üßæ Product Name */}
        <h1 className="text-3xl font-bold mb-3">{name}</h1>

        {/* üñºÔ∏è Product Image */}
        <img
          src={selectedVariant.image}
          alt={name}
          className="rounded-xl object-contain mb-4"
        />

        {/* üé® Color Selectors */}
        <div className="flex gap-3 mt-2 justify-center">
          {variants.map((v, i) => (
            <button
              key={i}
              onClick={() => setSelectedColor(i)}
              className={`w-6 h-6 rounded-full border-2 transition ${
                selectedColor === i
                  ? "border-black scale-110"
                  : "border-gray-300"
              }`}
              style={{ backgroundColor: v.colorHex }}
            />
          ))}
        </div>
      </div>

      {/* RIGHT SECTION */}
      <div className="bg-white p-6 rounded-2xl shadow-lg w-full md:w-1/2">
        {/* üí∞ Pricing */}
        <div className="mb-4">
          <p className="text-gray-500 line-through text-lg">
            ‚Çπ{selectedVariant.mrp.toLocaleString()}
          </p>
          <p className="text-3xl font-bold text-blue-700">
            ‚Çπ{selectedVariant.price.toLocaleString()}
          </p>
        </div>

        {/* üè∑Ô∏è Description */}
        <p className="text-gray-600 font-semibold mb-6">{product.description}</p>

        <div className="space-y-2">
          {emiPlans.map((plan, i) => (
            <div
              key={i}
              className="flex justify-between items-center py-3 border-b border-gray-100"
            >
              <div>
                <p>
                  ‚Çπ{plan.monthlyAmount?.toLocaleString() || "‚Äî"} √ó{" "}
                  {plan.tenureMonths} months
                </p>
                <p className="text-green-600 text-sm">
                  Cashback ‚Çπ{plan.cashbackValue?.toLocaleString() || 0}
                </p>
              </div>
              <p className="text-sm text-gray-600">
                {plan.interestRatePercent}% interest
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
